            this.width = 600;
            this.height = 300;
            this.scale = 1; // 缩放比例

            //舞台画布
            var renderType = location.search.indexOf('dom') != -1 ? 'dom' : 'canvas';
            
            // 舞台是可视对象树的根，可视对象只有添加到舞台或其子对象后才会被渲染出来。
