            //     width: 320,
            //     height: 480,
            //     background: 'lightblue' // 背景颜色
            // });
            
            this.stage = new Hilo.Stage({
                renderType: renderType,
                container: document.getElementById('game-container'), 
                width: this.width,
                height: this.height,
                scaleX: this.scale,
                scaleY: this.scale,
                background: 'lightblue',
                align : Hilo.align.CENTER
            });
            // document.body.appendChild(this.stage.canvas);

            // Ticker是一个定时器类。它可以按指定帧率重复运行，从而按计划执行代码。
            //启动计时器
            this.ticker = new Hilo.Ticker(0);
            // // 添加定时器对象。定时器对象必须实现 tick 方法。
            // // 需要把Tween加到ticker里才能使用
            this.ticker.addTick(Hilo.Tween); // Tween类提供缓动功能。
            this.ticker.addTick(this.stage);
            this.ticker.start(true); // 启动定时器。

            //绑定交互事件
            // POINTER_START - 鼠标或触碰开始事件。对应touchstart或mousedown。
            this.stage.enableDOMEvent(Hilo.event.POINTER_START, true);
            // this.stage.on(Hilo.event.POINTER_START, this.onUserInput.bind(this));

            //Space键控制
            if (document.addEventListener) {
                document.addEventListener('keydown', function(e) {
                    if (e.keyCode === 32) this.onUserInput(e);
                }.bind(this));
            } else {
                document.attachEvent('onkeydown', function(e) {
                    if (e.keyCode === 32) this.onUserInput(e);
                }.bind(this));
            }

            // //舞台更新
            this.stage.onUpdate = this.onUpdate.bind(this);

            //初始化
            this.initBackground();
            this.initScenes();
            this.initHoldbacks();
            this.initTrex();
            // this.initCurrentScore();

            //准备游戏
            this.gameReady();
            // 1.创建舞台
        // 舞台Stage是一个各种图形、精灵动画等的总载体。所以可见的对象都要添加到舞台或其子容器后，才会被渲染出来。
        // this.stage = new Hilo.Stage({
        //     renderType: 'canvas',
